/** Action metadata for accessing data related to the current view */
export interface IViewDataActionContext<T> {
  filteredItems: T[];
  data: GroupedViewItem<T>[];
  viewState: GroupedViewState;
  viewPreferences?: ViewPreferences;
}

interface AnyDataProvider<T> {
  get data(): GroupedViewItem<T>[];
  get filteredItems(): T[];
}

/** Class for action metadata for accessing data related to the current view */
export class ViewDataActionContext<T> implements IViewDataActionContext<T> {
  private provider: AnyDataProvider<T>;

  public viewState: GroupedViewState;
  public viewPreferences?: ViewPreferences;

  constructor(provider: AnyDataProvider<T>, viewState: GroupedViewState, viewPreferences?: ViewPreferences) {
    this.provider = provider;
    this.viewState = viewState;
    this.viewPreferences = viewPreferences;
  }

  get filteredItems(): T[] {
    return this.provider.filteredItems;
  }

  get data(): GroupedViewItem<T>[] {
    return this.provider.data;
  }
}
